import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

// https://api.nasa.gov/planetary/apod?api_key=DEMO_KEY&date=2023-02-10





function App() {

  const [apod, setApod] = useState();
  const [loaded, setLoaded] = useState(false);


  function fetchAPOD(dateParam) {
    setLoaded(false)

    axios
      .get("https://api.nasa.gov/planetary/apod", {
        params: {
          api_key: "C0VdGGwA3bv8MCl4auZDpbBqG5mIl1nDiLKIAf20",
          date: dateParam,
          thumbs: true,
        }
      })
      .then(function (response) {
        console.log(response);
        setApod(response.data);
        setLoaded(true)
      })
      .catch(function (error) {
        console.log(error);
        setLoaded(false)
      })
      .finally(function () {
        console.log("axios apod");
        // always executed
      });
  };

  useEffect(() => {
    fetchAPOD("2022-04-27");
  }, [])

  return (
    <div className="App">
      <p>
        NASA uygulamasÄ±nÄ± yapmak iÃ§in RADME.md dosyasÄ±ndaki talimatlarÄ± takip edin.
        Ä°yi eÄŸlenceler!
        {loaded && (
          <div>
            {apod.date}
            {apod.explanation}
            {apod.hdurl}
            {apod.media_type}
            {apod.title}
            {apod.url}
          </div>
        )}
        <span role="img" aria-label='go!'>ðŸš€ </span>
      </p>

    </div>
  );
}




export default App;
